import React, { useState, useEffect } from 'react'
import axios from 'axios'

const APIKey = '7ESCVeg0KgkQwgRo1PtacPmPisCPQwvP'

function CamList() {
    const [theCamList, setTheCamList] = useState([])

    function getList() {
        axios.get(`https://api.windy.com/api/webcams/v2/list/nearby=42.44,3.14,100,show=webcams:image?key=${APIKey}`)
            .then(response => setTheCamList(response.data.result.webcams))
            .then(console.log(theCamList))
    }
    useEffect(() => {
        getList()
    }, [])

    console.log(theCamList)
    return (
        <>
            {theCamList.map((cam) => { 
        return (
            <h1>{cam.player}</h1>
        )})}
        </>
    )


}

export default CamList
