import React, {useState, useEffect, useContext} from 'react'
import CamList from './Page Cams/CamList'
import {ContextCountry} from '../Context/ContextCountry'
import { ContextContinent } from '../Context/ContextContinent'

function Location()  {
    const countries = useContext(ContextCountry)
    const continents = useContext(ContextContinent)
    const [status, setStatus] = useState('choice')
    const [category, setCategory] = useState('beach')
    const continentsSelction = ['Europe', 'Asie', 'Océanie', 'Afrique', 'Amérique du Nord', 'Amérique du Sud']
    const [continent, setContinent] = useState()
    const [country, setCountry] = useState()
    const [continentCode, setContinentCode] = countries.map(country => {return (country.continent === continent ? country.codeContinent : "")})
    const [countryCode, setCountryCode] = useState()

    switch (status) {
        case "results":
            return <CamList category={category} continent={continentCode} country={countryCode} />;
        case "choice":
    return (
        <>
        <h4>Choisis un continent :</h4>
            {continents.map(continent => (
                    <>
                        <button key={continent} onClick={() => setContinent(continent)}> {continent} </button>

                    </>
            ))}

        <h4>Choisis un pays :</h4>
            {countries.map(country =>(
                    <> 
                    <button key={country} onClick={() => setCountry(country)}> {country} </button>
                    </>
            ))}

        </>
    ) 
}
}

export default Location
