import React, { useState, useEffect } from 'react'
import axios from 'axios'
import Iframe from "react-iframe";

const APIKey = '7ESCVeg0KgkQwgRo1PtacPmPisCPQwvP'

function CamList() {
    const [theCamList, setTheCamList] = useState([])
    const [webcam, setWebcam] = useState(false);
    const [hasError, setErrors] = useState(false);

    function getList() {
        axios.get(`https://api.windy.com/api/webcams/v2/list/nearby=42.44,3.14,100?show=webcams:image,location,player&key=${APIKey}`)
            .then((res) => {
                const { result } = res;
                const { webcams } = result;
                const [firstWebcam] = webcams;
                console.log(firstWebcam);
                setWebcam(firstWebcam);
            })
            .catch((err) => setErrors(err));
    }
    useEffect(() => {
        getList()
    }, [])

    console.log(theCamList)
    return (
        <>
            {theCamList.map((cam) => { 
        return (
            <>
            <h1>{cam.title}</h1>
            <Iframe url={webcam.player.lifetime.embed}/>
            </>
        )})}
        </>
    )


}

export default CamList
