import React, { useState, useContext } from 'react'
import { ContextContinent } from '../Context/ContextContinent';
import { ContextCountry } from '../Context/ContextCountry';
import CountryList from './CountryList';

const ContinentList = () => {
    const [continents, setContinents] = useContext(ContextContinent)
    const [chosenContinent, setChosenContinent] = ""
    const [countries, setCountries] = useContext(ContextCountry)
    return (
        <>
            {continents.map(continent => {
                return (
                    <>
                    <button onClick={setChosenContinent(continent.name)}> {continent.name} </button>
                    <CountryList continent={continent.name} />
                    </>
            )})}




        </>
    )
}
export default ContinentList
